import React, { useState } from 'react';
import { Button, Input, VStack, Center, Square, Circle } from 'native-base';
import { Alert } from '../../components/Alert';
import imgLogo from '../../../assets/logo.png';

export function Login() {
  const [cpfValue, setCpfValue] = useState('');
  const [showAlert, setShowAlert] = useState(false);

  //validação para de exemplo
  function isLoginValid(login) {
    return login.length >= 6;
  }

  function formatCPF(value) {
    value = value.replace(/\D/g, '');
    value = value.replace(/(\d{3})(\d)/, '$1.$2');
    value = value.replace(/(\d{3})(\d)/, '$1.$2');
    value = value.replace(/(\d{3})(\d{1,2})$/, '$1-$2');
    return value;
  }

  function handleCPFChange(event) {
    const inputValue = event.target.value;
    const formattedCPF = formatCPF(inputValue);
    setShowAlert(false)
    setCpfValue(formattedCPF);
  }

  return (
    <VStack space={4} p={4}>
      <img src={imgLogo} />

      <Center>
      <Center _text={{
      color: "blue",
      
    }} >
        Ajude a construir benefícios para você!
      </Center>
    </Center>

      <Input
        variant="underlined"
        placeholder="CPF..."
        w="100%"
        size="lg"
        value={cpfValue}
        onChange={handleCPFChange}
        maxLength="15"
      />
      <Input
        variant="underlined"
        placeholder="Senha..."
        w="100%"
        size="lg"
        type="password"
      />
      <Button
        size="lg"
        onClick={() => {
          const loginInput = "";
          if (!isLoginValid(loginInput)) {
            setShowAlert(true);
            console.log("Falha na autenticação")
          } else {
            setShowAlert(false);
            console.log("Login bem-sucedido")
          }
        }}
      >
        Entrar
      </Button>
      {showAlert && (
        <Alert
          title="Erro de login"
          description="Usuário ou senha incorretos"
          status="error"
        />
      )}

    <Box alignItems="center">
      <Pressable onPress={() => console.log("I'm Pressed")} rounded="8" overflow="hidden" borderWidth="1" borderColor="coolGray.300" maxW="96" shadow="3" bg="coolGray.100" p="5">
        <Box>
          <HStack alignItems="center">
            <Badge colorScheme="darkBlue" _text={{
            color: "white"
          }} variant="solid" rounded="4">
              Business
            </Badge>
            <Spacer />
            <Text fontSize={10} color="coolGray.800">
              1 month ago
            </Text>
          </HStack>
          <Text color="coolGray.800" mt="3" fontWeight="medium" fontSize="xl">
            Marketing License
          </Text>
          <Text mt="2" fontSize="sm" color="coolGray.700">
            Unlock powerfull time-saving tools for creating email delivery and
            collecting marketing data
          </Text>
          <Flex>
            <Text mt="2" fontSize={12} fontWeight="medium" color="darkBlue.600">
              Read More
            </Text>
          </Flex>
        </Box>
      </Pressable>
    </Box>
    </VStack>
  );
}
