import React, { useState } from 'react';
import { Button, Input, VStack } from 'native-base';
import { Alert } from '../../components/Alert';
import imgLogo from '../../../assets/logo.png';

export function Login() {
  const [cpfValue, setCpfValue] = useState('');
  const [showAlert, setShowAlert] = useState(false);

  // Função de validação para de exemplo
  function isLoginValid(login) {
    return login.length >= 6;
  }

  function formatCPF(value) {
    value = value.replace(/\D/g, '');
    value = value.replace(/(\d{3})(\d)/, '$1.$2');
    value = value.replace(/(\d{3})(\d)/, '$1.$2');
    value = value.replace(/(\d{3})(\d{1,2})$/, '$1-$2');
    return value;
  }

  function handleCPFChange(event) {
    const inputValue = event.target.value;
    const formattedCPF = formatCPF(inputValue);
    setCpfValue(formattedCPF);
  }

  return (
    <VStack space={4} p={4}>
      <img src={imgLogo} />

      <Input
        variant="underlined"
        placeholder="CPF..."
        w="100%"
        size="lg"
        value={cpfValue}
        onChange={handleCPFChange}
        maxLength="15"
      />
      <Input
        variant="underlined"
        placeholder="Senha..."
        w="100%"
        size="lg"
        type="password"
      />
      <Button
        size="lg"
        onClick={() => {
          const loginInput = ""; // Substitua pela entrada do usuário, por exemplo: loginInput = document.getElementById('login').value
          if (!isLoginValid(loginInput)) {
            setShowAlert(true);
          } else {
            setShowAlert(false);
            // Lógica para lidar com o login bem-sucedido
          }
        }}
      >
        Entrar
      </Button>
      {showAlert && (
        <Alert
          title="Erro de login"
          description="Usuário ou senha incorretos"
          status="error"
        />
      )}
    </VStack>
  );
}
